<?php

namespace Wapuugotchi\Wapuugotchi;

if ( ! defined( 'ABSPATH' ) ) : exit(); endif; // No direct access allowed.

class Menu {
	public function __construct() {
		add_action( 'admin_menu', [ $this, 'create_menu_page' ] );
	}

	public function create_menu_page() {
		$capability = 'manage_options';
		$slug       = 'wapuugotchi';

		add_menu_page(
			__( 'Wapuugotchi', 'wapuugotchi' ),
			__( 'WapuuGotchi', 'wapuugotchi' ),
			$capability,
			$slug. '-customizer',
			[ $this, 'customizer_page_template' ],
			'data:image/svg+xml;base64,'
		);

		add_submenu_page(
			$slug. '-customizer',
			__( 'Wapuugotchi', 'wapuugotchi' ),
			__( 'Customizer', 'wapuugotchi' ),
			$capability,
			$slug. '-customizer',
			[ $this, 'customizer_page_template' ]
		);
		add_submenu_page(
			$slug. '-customizer',
			__( 'Wapuugotchi', 'wapuugotchi' ),
			__( 'Quest Log', 'wapuugotchi' ),
			$capability,
			$slug . '-quests',
			[ $this, 'quests_page_template' ]
		);
	}

	public function customizer_page_template () {
		echo '<div class="wrap"><div id="wapuugotchi-app"></div></div>';
	}

	public function quests_page_template () {
		echo '<div class="wrap"><div id="wapuugotchi-quests"><h1>Quest Log</h1><div style="width: 100%; text-align: center"><img style="max-width: 500px; width: 100%;" src="data:image/svg+xml;base64," /></div></div></div>';
	}


}